import { makeStyles } from "@material-ui/core";

export default makeStyles((theme) => ({
    
    searchContainer: {
        width: '280px',
        display: 'flex',
        flexDirection: 'column',
        fontFamily: 'sans-serif',
        textAlign: 'center',
        justifyContent: 'center',
        alignItems: 'center',
    },
    dropdown: {
        backgroundColor: 'white',
        display: 'flex',
        flexDirection: 'column',
        border: '1px solid gray',
    },
    input: {
        width: '220px',
    },
    searchInner: {
        display: 'flex',
    },
    dropdownRow: {
        cursor: 'pointer',
        textAlign: 'start',
        margin: '2px 0',
    },
    // dropdown:empty: {
    //     border: 'none',
    // },

})) ;
