import React, { useState, useEffect } from 'react' ;
import axios from 'axios' ;
import { useParams } from 'react-router-dom' ;

import Form from 'react-bootstrap/Form' ;
import Button from 'react-bootstrap/Button' ;


const JournalForm = (props ) => {

    const { oldJournal } = props ;
    const [ errors, setErrors ] = useState({}) ;
    const [ quote, setQuote ] = useState( ) ;
    const [ journal, setJournal ] = useState(
        props.oldJournal || {
        entry: '',
        writtenBy: '',
        quoted: '',
    }) ;
    
    const { id } = useParams() ;
    
    useEffect(() => {
        axios.get(`http://localhost:8000/api/quotes/${id}`)
        .then((res) => {
            console.log(res.data) ;
            setQuote(res.data) ;
        })
        .catch((err) => {
            console.log("Error from DETAILS", err)
        })
    }, []) ;
    
    console.log('quote is ' + quote.quote) ;
    console.log(id) ;
    useEffect(()=> {
        if (props.oldJournal) {
            setJournal(props.oldJournal) ;
        }
    }, [props.oldJournal]) ;

    const submitHandler = (e) => {
        e.preventDefault() ;
        props.submitHandler(journal, setErrors) ;
    } ;

    const changeHandler = (e) => {
        setJournal( e.target.value) ;
        
    } ;

    return (
        <div className="container">
        <div className="add">
            <Form onSubmit={submitHandler} className="addForm">
                <Form.Group>
                    <Form.Label>Quote</Form.Label>
                    <Form.Control as ='textarea' rows={3} name='quoted' value={ quote.id}>{ quote.quote }</Form.Control>
                </Form.Group>
                <Form.Group className="mb-3">
                { errors.journal && <p className="error">{ errors.journal.entry }</p> }
                <Form.Label>Journal Entry</Form.Label>
                <Form.Control as='textarea' rows={3} name="entry" value={journal.entry} onChange={changeHandler} />
                </Form.Group>
                <Form.Group className="mb-3">
                    { errors.author && <p className="error">{ errors.journal.writtenBy }</p> }
                    <Form.Label>Written By</Form.Label>
                    <Form.Control type="text" name="writtenBy" value={journal.written} onChange={changeHandler} />
                </Form.Group>
                
                <Button variant="primary" type="submit"> {props.buttonText}
                </Button>
            </Form>
        </div>
    </div>
    )
}
export default JournalForm ;
