import React, { useEffect, useState } from 'react' ;
import axios from 'axios' ;

import { Grid, CircularProgress, Box, CardActions, Paper,  Button, Typography } from '@material-ui/core' ;

import useStyles from './Journal.style' ;
import Quote from '../Quote/Quote' ;

const Journal = () => {
    const [ journals, setJournals ] = useState([]) ;
    const classes = useStyles() ;
    
    useEffect(() => {
        axios.get('http://localhost:8000/api/journals')
        .then((res) => {
            setJournals(res.data)
        })
        .catch((err) => {
            console.log('ERROR from GET ALL') ;
        })
    }, []) ;

    return (
        !journals.length ? <CircularProgress /> : (
            <Grid className={ classes.container } container alignItems='stretch' spacing={3}>
                { journals.map((journal) => (
                    <Grid key={ journal.id } item xs={12} sm={6} >
                        <Box p={5}>
                            <Paper> 
                                <Box p={5}>
                                    
                                    <Typography variant='h5'> Quote </Typography>
                                    <Typography className={ classes.title } variant='body1' gutterBottom>Words by: { journal.quoted.quote.author }</Typography>
                                    <Typography variant='h5' color='textBold' component='p'> { journal.quoted.quote.quote }</Typography>
                                </Box>
                                <Box p={5}>
                                    <Typography variant='h5'> Journal Entry </Typography>
                                    <Typography variant='h5'> { journal.entry }  </Typography>
                                    <CardActions className={ classes.cardActions }>
                                        <Button size='small' color='primary' variant='contained' href='/journal/edit/:id'> Eidt </Button>
                                    </CardActions>
                                    <CardActions className={ classes.cardActions }>
                                        <Button size='small' color='primary' variant='contained' href='/journal/delete/:id'> delete </Button>
                                    </CardActions>
                                </Box>
                            </Paper>
                        </Box>
                    </Grid>
                ))}     
            </Grid>
        )
    ) ;
} 

export default Journal ;
