import React, { useEffect, useState } from 'react' ;
//import { useSelector } from 'react-redux'; 
import axios from 'axios' ;

import { Grid, CircularProgress, Card, CardActions, CardContent,  Button, Typography } from '@material-ui/core' ;

import useStyles from './Quote.style' ;

const Quote = () => {
    const [ quotes, setQuotes ] = useState([]) ;
    const classes = useStyles() ;
    
    useEffect(() => {
        axios.get('http://localhost:8000/api/quotes')
        .then((res) => {
            setQuotes(res.data)
        })
        .catch((err) => {
            console.log('ERROR from GET ALL') ;
        })
    }, []) ;

    return (
        !quotes.length ? <CircularProgress /> : (
            <Grid className={ classes.container } container alignItems='stretch' spacing={3}>
                { quotes.map((quote) => (
                    <Grid item xs={12} sm={6}>
                        <Card key={ quote.id }>
                            <CardContent >
                                <Typography className={ classes.title } variant='body1' gutterBottom>Words by: { quote.author }</Typography>
                                </CardContent>
                            <CardContent>
                                <Typography variant='h5' color='textBold' component='p'> { quote.quote }</Typography>
                            </CardContent>
                            <CardContent>
                                <Typography variant='body2' color='textSecondary' component='p'>Source: { quote.source }</Typography>
                            </CardContent>
                            <CardActions className={ classes.cardActions }>
                                <Button size='small' color='primary' variant='contained' href='/journal/add'> Journal </Button>
                            </CardActions>
                        </Card>
                    </Grid>
                ))}     
            </Grid>
        )
    ) ;
} 

export default Quote ;
