import React, { useState, useEffect } from 'react' ;
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import FileBase from 'react-file-base64' ;
import { useDispatch, useSelector } from 'react-redux' ;
import useStyles from './QuoteForm.styles' ;
import QuoteNew from '../Quote/QuoteNew' ;
import QuoteUpdate from '../Quote/QuoteUpdate' ;


const QuoteForm = ({ currentId, setCurrentId }) => {
    const classes = useStyles() ;
    const [ quoteData, setQuoteData ] = useState({
        quote: '', author: '', source: '' }) ; 
    const quote = useSelector((state) => currentId ? state.quote.find((p) => p._id === currentId) : null) ;
    const dispatch = useDispatch(); 

    useEffect(() => {
        if(quote) setQuoteData(quote) ;
    }, [quote]) ;

    const handleSubmit = (e) => {
        e.preventDefault() ;

        if(currentId) {
            dispatch(QuoteUpdate(currentId, quoteData))
        } else {
            dispatch(QuoteNew(quoteData)) ;
        }
        clear() ;
    }

    const clear = ()  => {
        setCurrentId(null) ;
        setQuoteData({ quote: '', author: '', source: '' }) ;
    }

    return (
        <div>
            <Paper className={ classes.paper }>
                <form autoComplete='off' noValidate className={ `${ classes.root} ${ classes.form }` } onSubmit={ handleSubmit }>
                    <Typography variant='h6'>{ currentId ? 'Editing' : 'Adding'} a Quote </Typography>
                    <TextField name='quote' variant='outlined' label='quote' fullwidth value={ quoteData.creator } onChange={ (e) => setQuoteData({ ...quoteData, quote: e.target.value }) } />

                    <TextField name='author' variant='outlined' label='author' fullwidth value={ quoteData.title } onChange={ (e) => setQuoteData({ ...quoteData, author: e.target.value }) } />

                    <TextField name='source' variant='outlined' label='source' fullwidth value={ quoteData.message } onChange={ (e) => setQuoteData({ ...quoteData, message: e.target.value }) } />

                    <div className={ classes.fileInput }>
                        <FileBase type='file' multiple={false} onDone={({base64}) => setQuoteData({ ...quoteData, selectedFile: base64 })} />
                    </div>
                    <Button className={ classes.buttonSubmit } variant='contained' color='primary' size='large' type='submit' fullwidth >Submit</Button>
                    <Button className={ classes.buttonSubmit } variant='contained' color='secondary' size='small'onClick={ clear } fullwidth >Clear</Button>
                </form>
            </Paper>


        </div>
    ) ;
}

export default QuoteForm ;
