import React, { useEffect, useState } from 'react';
import useStyles from './SearchBar.style';
import Search from '@material-ui/icons/Search';
import { TextField } from '@material-ui/core';
import axios from 'axios';
import Quotes from '../Quote/Quote' ;
import { useNavigate } from 'react-router-dom';


const SearchBar = ( props) => {
    const classes = useStyles();
    const [quotes, setQuotes] = useState([]);
    const [quote, setQuote] = useState([]);
    const navigate = useNavigate() ;

    useEffect(() => {
        axios.get('http://localhost:8000/api/quotes')
            .then((res) => {
                setQuotes(res.data)
            })
            .catch((err) => {
                console.log('ERROR from GET ALL');
            })
    }, []);


    const [value, setValue] = useState("");
    let data = quotes ;
    const onChange = (event) => {
        setValue(event.target.value);
    };

    const onSearch = (searchTerm) => {
        setValue(searchTerm);
        navigate('/')
        // our api to fetch the search result
        console.log("search ", searchTerm);
    };

    // const filteredQuotes = quotes.filter((el) => {
    //     //if no input the return the original
    //     if (props.input === '') {
    //         return el;
    //     }
    //     //return the item which contains the user input
    //     else {
    //         return el.text.includes(props.input)
    //     }
    // })

    return (
        <div className="search-container">
            <div className="search-inner">
                <input type="text" value={value} onChange={onChange} />
                <button onClick={() => onSearch(value)} > Search </button>
            </div>
            <div className="dropdown">
                {
                    data.filter((quote) => {
                        const searchTerm = value.toLowerCase();
                        const authorLastName = quote.authorLastName.toLowerCase();

                        return (
                            searchTerm &&
                            authorLastName.startsWith(searchTerm) &&
                            authorLastName !== searchTerm
                        );
                    })
                    .slice(0, 10)
                    .map((quote) => (
                        <div onClick={() => onSearch(quote.authorLastName)}
                            className="dropdown-row"
                            key={quote.authorLastName} > 
                            {quote.authorLastName} 
                        </div>
                    ))}
                
            </div>
        </div >
    );
}

export default SearchBar;
