import React, { useEffect, useState } from 'react';
import useStyles from './SearchBar.style';
import Search from '@material-ui/icons/Search';
import axios from 'axios';


const SearchBar = ({ placeholder, author }) => {
    const classes = useStyles();
    const [quotes, setQuotes] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/quotes')
            .then((res) => {
                setQuotes(res.data)
            })
            .catch((err) => {
                console.log('ERROR from GET ALL');
            })
    }, []);

    return (
        <div className={classes.search}>
            <div className={classes.searchInput}>
                <input type='text' placeholder={placeholder} className={classes.input} value={quotes} />
                {/* <div className={classes.icon }>
                    <div className={ classes.results}>
                { quotes.map((quote) => (
                    <a className={ classes.item } key={ quote._id } href={`/quote/${quote.authorLastName}`}>
                        <p>{ quote?.authorLastName } </p>
                    </a>
                )) } */}
                {/* <div className="icon">
                    {quotes.length === 0 ? (
                        <Search />
                    ) : (
                        // <CloseIcon id="clearBtn" onClick={clearInput} />
                    )}
                </div> */}
            </div>
            {quotes.length != 0 && (
                <div className="dataResult">
                    { quotes.map((quote) => (
                    <a className={ classes.item } key={ quote._id } href={`/quote/${quote.authorLastName}`}>
                        <p>{ quote?.authorLastName } </p>
                    </a>
                    ))}
                
                </div>
            )}
        </div>
    );
};

export default SearchBar;
