import React, {  useState, useEffect } from 'react' ;
import { Container, Typography, Paper } from '@material-ui/core';
import axios from 'axios' ;

import useStyles from './RandomQuote.styles' ;

const RandomQuote = () => {
    const classes = useStyles() ;
    const [ quotes, setQuotes ] = useState([]) ;
    const [ quote, setQuote ] = useState() ;

    let random = Math.random() ;
    let quoteIndex = Math.floor(random * quotes.length) ;
    let randomQuote = quotes[quoteIndex] ;

    useEffect(() => {
        let loading = true ;
        axios.get('http://localhost:8000/api/quotes')
        .then((res) => {
            loading = false ;
            setQuotes(res.data)
        })
        .catch((err) => {
            console.log('ERROR from GET ALL') ;
        })
    }, []) ;




    return (
        <Container className={ classes.randomQuote } maxWidth='md'>
            <Paper className={ classes.bluePaper } elevation={ 6 }>
                <Typography variant='h3'className={ classes.quotes }>
                    Random { randomQuote.quote }
                </Typography>

            </Paper>
        </Container>
    )
}

export default RandomQuote ;
