import React, { useEffect, useState } from 'react';
import useStyles from './SearchBar.style';
import Search from '@material-ui/icons/Search';
import { TextField } from '@material-ui/core';
import axios from 'axios';


const SearchBar = ( props) => {
    const classes = useStyles();
    const [quotes, setQuotes] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/quotes')
            .then((res) => {
                setQuotes(res.data)
            })
            .catch((err) => {
                console.log('ERROR from GET ALL');
            })
    }, []);
    console.log('quotes' + quotes) ;
    const filteredQuotes = quotes.filter((el) => {
        //if no input the return the original
        if (props.input === '') {
            return el;
        }
        //return the item which contains the user input
        else {
            return el.text.includes(props.input)
        }
    })
    
    return (
        <div className="main">
        
            <div className="search">
                <TextField
                    id="outlined-basic"
                    variant="outlined"
                    
                    label="Search By Author Last Name"
                />
            </div>
            <ul>
            {filteredQuotes.map((quote) => (
                <a  href={`/quote/${quote.authorLastName}`} className={classes.list} ><li key={quote.id} className={classes.list}>{quote.authorLastName}</li></a>
            ))}
        </ul>
        </div>
    );
}

export default SearchBar;
